/*==============================================================*/
/* Table: order_payment	 修改支付时和更新时间                                                                                      */
/*==============================================================*/

ALTER TABLE order_payment CHANGE payTime payTime TIMESTAMP null DEFAULT null;

ALTER TABLE order_payment CHANGE updateTime updateTime TIMESTAMP not null DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP;
/*==============================================================*/
/* Table: mail_queue                                           */
/*==============================================================*/
alter table mail_queue add column type tinyint DEFAULT NULL;

/*==============================================================*/
/* Table: mail_subscribe                                           */
/*==============================================================*/
alter table mail_subscribe add column planId int DEFAULT NULL;

/*==============================================================*/
/* Table: mail_template                                           */
/*==============================================================*/
TRUNCATE TABLE mail_template;
INSERT INTO mail_template VALUES ('100000', '新龙直销用户修改密码', '尊敬的${userName}<br/>\r\n您好！<br/>\r\n欢迎使用新龙直销会员帐号\"找回密码\"功能。请点击以下独立链接，直接进入您的新龙直销帐号重新设置密码：<br/>\r\n<a href=\"${findPassUrl}\">${findPassUrl}</a><br/>\r\n如果您没有申请找回密码，请忽略此邮件。祝您在新龙直销购物愉快，谢谢。<br/>\r\n请登陆<a href=\"http://www.${domainName }.com\">http://www.${domainName }.com</a>继续享受新龙直销带给您的购物享受。', '5');
INSERT INTO mail_template VALUES ('100001', '新龙直销新用户注册', '亲爱的新龙直销用户,<br/>您好！感谢您注册新龙直销商城。<br/>预祝您在新龙直销商城上购物愉快！<br/>新龙直销(http://www.xlbuy365.com)', '6');
INSERT INTO mail_template VALUES ('100002', '<#list brandNames as name>${name} </#list>即将开售，全场${minRebate}折起', '<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>品牌订阅</title>\r\n</head>\r\n<body>\r\n<#function thumbnail img type>\r\n<#return fileThumbPath + img + \'_\' + type + img?substring(img?last_index_of(\'.\'))>\r\n</#function>\r\n<table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n   <tbody>\r\n    <tr>\r\n      <td align=\"center\" style=\"background-color:#fff;\" height=\"10\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n          <tbody>\r\n            <tr>\r\n              <td style=\"font-size:12px; color:#999\" width=\"330\"> 如果邮件没有正常显示，请直接访问 <a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${contextPath }\"> 新龙直销</a></td>\r\n              <td align=\"right\" style=\"font-size:12px; color:#999;\"> 如果您不愿意继续接收，请:<a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${mailUrl}\"> 退订本类邮件</a></td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td height=\"10\"></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\"><table width=\"750\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px;color:#333;font-family:tahoma,arial,5b8b4f53,sans-serif;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td align=\"center\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\" style=\"font-size:12px; line-height:18px;\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"319\" height=\"89\" valign=\"bottom\"><a target=\"_blank\" href=\"${contextPath }\"> <img style=\"color:#e95705; font-size:36px; font-weight:bold; line-height:40px;\" border=\"0\" src=\"${imagesPath}/uploadfolder/logo.jpg\" width=\"319\" height=\"89\"> </a></td>\r\n                      <td width=\"460\" align=\"right\" valign=\"bottom\"><img border=\"0\" src=\"${imagesPath}/mail/des1.gif\" width=\"222\" height=\"50\" alt=\"新龙描述\" /></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"10\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td valign=\"top\" align=\"center\" height=\"42\"><table style=\"background:#ff5702;\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" height=\"38\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"5\"></td>\r\n                        <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/default.htm\">全部特卖</a></td>\r\n                      <#list productCategorys as pcs>\r\n                      <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/cate-${pcs.productCategoryId?c}.htm\">${pcs.name}</a></td>\r\n                     \r\n                      </#list>\r\n                      <td>&nbsp;</td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n           \r\n           <#list plans as plan>\r\n            <tr>\r\n              <td align=\"center\" valign=\"top\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td height=\"18\" colspan=\"4\" align=\"left\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                          <tr>\r\n                            <td><div style=\" color:#fc7c00;font-size:18px; line-height:18px; font-weight:bold;\">·${plan.name}</div></td>\r\n                            <td width=\"100\" align=\"right\"></td>\r\n                          </tr>\r\n                        </table></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"15\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td colspan=\"3\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border:1px solid #ccc5c2; background-color:#ffffff;\">\r\n                          <tbody>\r\n                          <tr>\r\n                            <td align=\"center\">\r\n                            <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                <tbody>\r\n                                <tr>\r\n                                  <td height=\"100\">\r\n                                  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tbody>\r\n                                      <tr>\r\n                                        <td width=\"258\" align=\"center\">\r\n	                                        <a href=\"${contextPath}/brand-${plan.brandId?c}.htm\" target=\"_blank\">\r\n	                                        	<img border=\"0\" src=\"${thumbnail(plan.brand.logoImageUrl, \'t1\')}\" alt=\"${plan.brand.name}\" width=\"150\" height=\"75\">\r\n	                                        </a>\r\n                                        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                 </table>\r\n                                 </td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td>\r\n                                  <table width=\"100%\" height=\"49\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background:url(${imagesPath}/mail/des2.gif) no-repeat;\">\r\n                                      <tbody><tr>\r\n                                        <td width=\"135\" align=\"center\"><span style=\"font-size:36px; font-weight:bold; color:#ffffff; font-family:Tahoma, Geneva, sans-serif;\">${plan.minRebate}</span><span style=\"font-size:18px; font-weight:bold; color:#ffffff; vertical-align:text-bottom;\">折起</span></td>\r\n                                        <td align=\"left\"><a href=\"${contextPath}/brand-${plan.brandId?c}.htm\" target=\"_blank\"><img border=\"0\" src=\"${imagesPath}/mail/btn2.gif\" width=\"85\" height=\"32\" alt=\"去看看\"></a></td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                  </table>\r\n                                  </td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td>\r\n                                  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tbody>\r\n                                      <tr>\r\n                                        <td width=\"258\" height=\"30\" align=\"center\" style=\"font-size:12px; color:#333333;\">\r\n                                        ${plan.startTime?string(\"yyyy/MM/dd\")}—${plan.endTime?string(\"yyyy/MM/dd\")}\r\n                                        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                  </table>\r\n                                  </td>\r\n                                </tr>\r\n                              </tbody>\r\n                              </table>\r\n                              </td>\r\n                            <td width=\"420\">\r\n                            	<a href=\"${contextPath}/plan-${plan.planId?c}.htm\" target=\"_blank\">\r\n                            		<img border=\"0\" src=\"${thumbnail(plan.imageUrl, \'t13\')}\" alt=\"${plan.name}\" width=\"420\" height=\"180\">\r\n                            	</a>\r\n                            </td>\r\n                          </tr>\r\n                        </tbody></table></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"10\"></td>\r\n                    </tr>\r\n                    \r\n                    <tr>\r\n                      <td height=\"12\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td colspan=\"3\" align=\"right\"><a target=\"_blank\" href=\"${contextPath}/default.htm\" style=\"font-size:12px; line-height:18px; color:#fc7c00;text-decoration:underline;\"> 查看更多&gt;&gt;</a></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n           </#list>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n              <tr>\r\n              <td height=\"74\" valign=\"middle\" align=\"center\" style=\"border-top:1px solid #dfdfdf;\">\r\n              	<table style=\"padding:0 10px;\">\r\n              		<tr>\r\n              			<td width=\"600\" height=\"55\" align=\"left\" valign=\"middle\">\r\n              				<span style=\"font-size:14px;\">欲知更多详情请登录 <a style=\"color:#f68333;\" href=\"${contextPath }\" target=\"_blank\">www.xlbuy365.com</a> 查询</span>&nbsp;\r\n              				<span style=\"font-size:14px;\">如果您不想继续收到此类邮件，请 <a style=\"color:#333;\" href=\"${mailUrl};\">点击退订</a></span>\r\n              			</td>\r\n              			<td rowspan=\"2\" height=\"169\" valign=\"middle\">\r\n              				<img src=\"${imagesPath}/mail/QR_Code.jpg\" width=\"122\" hieght=\"122\" border=\"0\" />\r\n              			</td>\r\n              		</tr>\r\n              		<tr>\r\n              			<td valign=\"top\" align=\"left\">\r\n              				<span>\r\n              					<a href=\"http://e.weibo.com/326060800\"><img style=\"vertical-align:-10px;\" src=\"http://img.t.sinajs.cn/t4/style/images/staticlogo/download/Buttons/32x32/Weibo_Buttons_32x32_yellow_back.png\" width=\"32\" height=\"32\" border=\"0\"/></a>\r\n              					<a href=\"http://e.weibo.com/326060800\" style=\"font-size:14px;color:#333;\">关注新浪微博</a>\r\n              				</span>\r\n              				\r\n              			</td>\r\n              		</tr>\r\n              	</table>\r\n              </td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\">\r\n      	<table width=\"730\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px; color:#999;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"25\" align=\"left\"></td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n       </td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n</body>\r\n</html>\r\n', '3');
INSERT INTO mail_template VALUES ('100003', '新龙直销一周品牌特卖预告，<#list brandNames as name>${name} </#list>低至${minRebate}折', '<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>特卖订阅</title>\r\n</head>\r\n<body>\r\n<#function thumbnail img type>\r\n<#return fileThumbPath + img + \'_\' + type + img?substring(img?last_index_of(\'.\'))>\r\n</#function>\r\n<table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n   <tbody>\r\n    <tr>\r\n      <td align=\"center\" style=\"background-color:#fff;\" height=\"10\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n          <tbody>\r\n            <tr>\r\n              <td style=\"font-size:12px; color:#999\" width=\"330\"> 如果邮件没有正常显示，请直接访问 <a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${contextPath }\"> 新龙直销</a></td>\r\n              <td align=\"right\" style=\"font-size:12px; color:#999;\"> 如果您不愿意继续接收，请:<a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${mailUrl}\"> 退订本类邮件</a></td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td height=\"10\"></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\"><table width=\"750\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px;color:#333;font-family:tahoma,arial,5b8b4f53,sans-serif;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td align=\"center\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\" style=\"font-size:12px; line-height:18px;\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"319\" height=\"89\" valign=\"bottom\"><a target=\"_blank\" href=\"${contextPath }\"> <img style=\"color:#e95705; font-size:36px; font-weight:bold; line-height:40px;\" border=\"0\" src=\"${imagesPath}/uploadfolder/logo.jpg\" width=\"319\" height=\"89\"> </a></td>\r\n                      <td width=\"460\" align=\"right\" valign=\"bottom\"><img border=\"0\" src=\"${imagesPath}/mail/des1.gif\" width=\"222\" height=\"50\" alt=\"新龙描述\" /></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"10\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td valign=\"top\" align=\"center\" height=\"42\"><table style=\"background:#ff5702;\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" height=\"38\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"5\"></td>\r\n                        <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/default.htm\">全部特卖</a></td>\r\n                      <#list productCategorys as pcs>\r\n                      <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/cate-${pcs.productCategoryId?c}.htm\">${pcs.name}</a></td>\r\n                     \r\n                      </#list>\r\n                      <td>&nbsp;</td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n           \r\n            <#if mostPopularBrands??>\r\n            <tr>\r\n              <td align=\"center\" valign=\"top\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td height=\"18\" colspan=\"3\" align=\"left\"><div style=\" color:#fc7c00;font-size:18px; line-height:18px; font-weight:bold;\">·正在热卖</div></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"15\"></td>\r\n                    </tr>\r\n                    <#list mostPopularBrands as plan>\r\n                    <tr>\r\n                      <td><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border:1px solid #ccc5c2; background-color:#ffffff;\">\r\n                          <tr>\r\n                            <td align=\"center\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                <tr>\r\n                                  <td height=\"100\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tr>\r\n                                        <td width=\"258\" align=\"center\">\r\n	                                        <a href=\"${contextPath}/brand-${plan.brand.brandId?c}.htm\" target=\"_blank\">\r\n	                                        	<img border=\"0\" src=\"${thumbnail(plan.brand.logoImageUrl, \'t1\')}\" alt=\"${plan.brand.name}\" width=\"150\" height=\"75\">\r\n	                                        </a>\r\n                                        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </table></td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td><table width=\"100%\" height=\"49\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background:url(${imagesPath}/mail/des2.gif) no-repeat;\">\r\n                                      <tr>\r\n                                        <td width=\"135\" align=\"center\"><span style=\"font-size:36px; font-weight:bold; color:#ffffff; font-family:Tahoma, Geneva, sans-serif;\">\r\n                                        <#if plan.products[0]??>\r\n                                        	${plan.products[0].rebate}\r\n                                        </#if>\r\n                                        </span><span style=\"font-size:18px; font-weight:bold; color:#ffffff; vertical-align:text-bottom;\">折起</span></td>\r\n                                        <td align=\"left\"><a href=\"${contextPath}/brand-${plan.brand.brandId?c}.htm\" target=\"_blank\"><img border=\"0\" src=\"${imagesPath}/mail/btn2.gif\" width=\"85\" height=\"32\" alt=\"去看看\" /></a></td>\r\n                                      </tr>\r\n                                    </table></td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tr>\r\n                                        <td width=\"258\" height=\"30\" align=\"center\" style=\"font-size:12px; color:#333333;\">${plan.startTime?string(\"yyyy/MM/dd\")}—${plan.endTime?string(\"yyyy/MM/dd\")}</td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </table></td>\r\n                                </tr>\r\n                              </table></td>\r\n                            <td width=\"420\">\r\n	                            <a href=\"${contextPath}/plan-${plan.planId?c}.htm\" target=\"_blank\">\r\n	                            	<img border=\"0\" src=\"${thumbnail(plan.imageUrl, \'t13\')}\" alt=\"${plan.name}\" width=\"420\" height=\"180\">\r\n	                            </a>\r\n                            </td>\r\n                          </tr>\r\n                        </table></td>\r\n                    </tr>\r\n                     <#if plan_index=1><#break></#if>\r\n                    <tr>\r\n                      <td height=\"10\"></td>\r\n                    </tr>\r\n                    </#list>\r\n                    <tr>\r\n                      <td height=\"15\"></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            </#if>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n              <tr>\r\n              <td height=\"74\" valign=\"middle\" align=\"center\" style=\"border-top:1px solid #dfdfdf;\">\r\n              	<table style=\"padding:0 10px;\">\r\n              		<tr>\r\n              			<td width=\"600\" height=\"55\" align=\"left\" valign=\"middle\">\r\n              				<span style=\"font-size:14px;\">欲知更多详情请登录 <a style=\"color:#f68333;\" href=\"${contextPath }\" target=\"_blank\">www.xlbuy365.com</a> 查询</span>&nbsp;\r\n              				<span style=\"font-size:14px;\">如果您不想继续收到此类邮件，请 <a style=\"color:#333;\" href=\"${mailUrl};\">点击退订</a></span>\r\n              			</td>\r\n              			<td rowspan=\"2\" height=\"169\" valign=\"middle\">\r\n              				<img src=\"${imagesPath}/mail/QR_Code.jpg\" width=\"122\" hieght=\"122\" border=\"0\" />\r\n              			</td>\r\n              		</tr>\r\n              		<tr>\r\n              			<td valign=\"top\" align=\"left\">\r\n              			<span>\r\n              					<a href=\"http://e.weibo.com/326060800\"><img style=\"vertical-align:-10px;\" src=\"http://img.t.sinajs.cn/t4/style/images/staticlogo/download/Buttons/32x32/Weibo_Buttons_32x32_yellow_back.png\" width=\"32\" height=\"32\" border=\"0\"/></a>\r\n              					<a href=\"http://e.weibo.com/326060800\" style=\"font-size:14px;color:#333;\">关注新浪微博</a>\r\n              				</span>\r\n              			\r\n              			</td>\r\n              		</tr>\r\n              	</table>\r\n              </td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\">\r\n      	<table width=\"730\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px; color:#999;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"25\" align=\"left\"></td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n       </td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n</body>\r\n</html>\r\n', '1');
INSERT INTO mail_template VALUES ('100004', '<#list planNames as name>${name} </#list>即将开售，全场${minRebate}折起', '<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>即将上线定阅</title>\r\n</head>\r\n<body>\r\n<#function thumbnail img type>\r\n<#return fileThumbPath + img + \'_\' + type + img?substring(img?last_index_of(\'.\'))>\r\n</#function>\r\n<table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n   <tbody>\r\n    <tr>\r\n      <td align=\"center\" style=\"background-color:#fff;\" height=\"10\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n          <tbody>\r\n            <tr>\r\n              <td style=\"font-size:12px; color:#999\" width=\"330\"> 如果邮件没有正常显示，请直接访问 <a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${contextPath }\"> 新龙直销</a></td>\r\n              <td align=\"right\" style=\"font-size:12px; color:#999;\"> 如果您不愿意继续接收，请:<a target=\"_blank\" style=\"color:#6699ff; text-decoration:none;\" href=\"${mailUrl}\"> 退订本类邮件</a></td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td height=\"10\"></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\"><table width=\"750\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px;color:#333;font-family:tahoma,arial,5b8b4f53,sans-serif;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td align=\"center\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\" style=\"font-size:12px; line-height:18px;\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"319\" height=\"89\" valign=\"bottom\"><a target=\"_blank\" href=\"${contextPath }\"> <img style=\"color:#e95705; font-size:36px; font-weight:bold; line-height:40px;\" border=\"0\" src=\"${imagesPath}/uploadfolder/logo.jpg\" width=\"319\" height=\"89\"> </a></td>\r\n                      <td width=\"460\" align=\"right\" valign=\"bottom\"><img border=\"0\" src=\"${imagesPath}/mail/des1.gif\" width=\"222\" height=\"50\" alt=\"新龙描述\" /></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"10\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td valign=\"top\" align=\"center\" height=\"42\"><table style=\"background:#ff5702;\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" height=\"38\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td width=\"5\"></td>\r\n                       <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/default.htm\">全部特卖</a></td>\r\n                       <#list productCategorys as pcs>\r\n                      <td valign=\"middle\" align=\"center\" width=\"110\"><a style=\"font-size:14px; color:#fff; text-decoration:none;\" target=\"_blank\" href=\"${contextPath }/cate-${pcs.productCategoryId?c}.htm\">${pcs.name}</a></td>\r\n                     \r\n                      </#list>\r\n                      <td></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n            <#list plans as plan>\r\n            <tr>\r\n              <td align=\"center\" valign=\"top\"><table width=\"730\" cellpadding=\"0\" cellspacing=\"0\">\r\n                  <tbody>\r\n                    <tr>\r\n                      <td height=\"18\" colspan=\"4\" align=\"left\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                          <tr>\r\n                            <td><div style=\" color:#fc7c00;font-size:18px; line-height:18px; font-weight:bold;\">·${plan.name}</div></td>\r\n                            <td width=\"100\" align=\"right\"></td>\r\n                          </tr>\r\n                        </table></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"15\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td colspan=\"3\"><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"border:1px solid #ccc5c2; background-color:#ffffff;\">\r\n                          <tbody>\r\n                          <tr>\r\n                            <td align=\"center\">\r\n                            <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                <tbody>\r\n                                <tr>\r\n                                  <td height=\"100\">\r\n                                  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tbody>\r\n                                      <tr>\r\n                                        <td width=\"258\" align=\"center\">\r\n	                                        <a href=\"${contextPath}/brand-${plan.brandId?c}.htm\" target=\"_blank\">\r\n	                                        	<img border=\"0\" src=\"${thumbnail(plan.brand.logoImageUrl, \'t1\')}\" alt=\"${plan.brand.name}\" width=\"150\" height=\"75\">\r\n	                                        </a>\r\n                                        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                 </table>\r\n                                 </td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td>\r\n                                  <table width=\"100%\" height=\"49\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" style=\"background:url(${imagesPath}/mail/des2.gif) no-repeat;\">\r\n                                      <tbody><tr>\r\n                                        <td width=\"135\" align=\"center\"><span style=\"font-size:36px; font-weight:bold; color:#ffffff; font-family:Tahoma, Geneva, sans-serif;\">${plan.minRebate}</span><span style=\"font-size:18px; font-weight:bold; color:#ffffff; vertical-align:text-bottom;\">折起</span></td>\r\n                                        <td align=\"left\"><a href=\"${contextPath}/brand-${plan.brandId?c}.htm\" target=\"_blank\"><img border=\"0\" src=\"${imagesPath}/mail/btn2.gif\" width=\"85\" height=\"32\" alt=\"去看看\"></a></td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                  </table>\r\n                                  </td>\r\n                                </tr>\r\n                                <tr>\r\n                                  <td>\r\n                                  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                                      <tbody>\r\n                                      <tr>\r\n                                        <td width=\"258\" height=\"30\" align=\"center\" style=\"font-size:12px; color:#333333;\">\r\n                                        ${plan.startTime?string(\"yyyy/MM/dd\")}—${plan.endTime?string(\"yyyy/MM/dd\")}\r\n                                        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                  </table>\r\n                                  </td>\r\n                                </tr>\r\n                              </tbody>\r\n                              </table>\r\n                              </td>\r\n                            <td width=\"420\">\r\n                            	<a href=\"${contextPath}/plan-${plan.planId?c}.htm\" target=\"_blank\">\r\n                            		<img border=\"0\" src=\"${thumbnail(plan.imageUrl, \'t13\')}\" alt=\"${plan.name}\" width=\"420\" height=\"180\">\r\n                            	</a>\r\n                            </td>\r\n                          </tr>\r\n                        </tbody></table></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"10\"></td>\r\n                    </tr>\r\n                    \r\n                    <tr>\r\n                      <td height=\"12\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td colspan=\"3\" align=\"right\"><a target=\"_blank\" href=\"${contextPath}/default.htm\" style=\"font-size:12px; line-height:18px; color:#fc7c00;text-decoration:underline;\"> 查看更多&gt;&gt;</a></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n           </#list>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"74\" valign=\"middle\" align=\"center\" style=\"border-top:1px solid #dfdfdf;\">\r\n            	<table style=\"padding:0 10px;\">\r\n              		<tr>\r\n              			<td width=\"600\" height=\"55\" valign=\"middle\" align=\"left\">\r\n              				<span style=\"font-size:14px;\">欲知更多详情请登录 <a style=\"color:#f68333;\" href=\"${contextPath }\" target=\"_blank\">www.xlbuy365.com</a> 查询</span>&nbsp;\r\n              				<span style=\"font-size:14px;\">如果您不想继续收到此类邮件，请 <a style=\"color:#333;\" href=\"${mailUrl};\">点击退订</a></span>\r\n              			</td>\r\n              			<td rowspan=\"2\" height=\"169\" valign=\"middle\">\r\n              				<img src=\"${imagesPath}/mail/QR_Code.jpg\" width=\"122\" hieght=\"122\" border=\"0\" />\r\n              			</td>\r\n              		</tr>\r\n              		<tr>\r\n              			<td valign=\"top\" align=\"left\">\r\n              				<span>\r\n              					<a href=\"http://e.weibo.com/326060800\"><img style=\"vertical-align:-10px;\" src=\"http://img.t.sinajs.cn/t4/style/images/staticlogo/download/Buttons/32x32/Weibo_Buttons_32x32_yellow_back.png\" width=\"32\" height=\"32\" border=\"0\"/></a>\r\n              					<a href=\"http://e.weibo.com/326060800\" style=\"font-size:14px;color:#333;\">关注新浪微博</a>\r\n              				</span>\r\n              				\r\n              			</td>\r\n              		</tr>\r\n              	</table>\r\n              </td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n    <tr>\r\n      <td align=\"center\" valign=\"top\">\r\n      	<table width=\"730\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"font-size:12px; line-height:18px; color:#999;\">\r\n          <tbody>\r\n            <tr>\r\n              <td height=\"25\" align=\"left\"></td>\r\n            </tr>\r\n          </tbody>\r\n        </table>\r\n       </td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n</body>\r\n</html>\r\n', '7');

