/*==============================================================*/
/* Table: supplier                                                */
/*==============================================================*/
DROP TABLE IF EXISTS `supplier`;
CREATE TABLE `supplier` (
  `supplierId` int(11) NOT NULL AUTO_INCREMENT,
  `brandName` varchar(128) NOT NULL COMMENT '品牌名称',
  `companyName` varchar(128) DEFAULT NULL COMMENT '公司名称',
  `contactName` varchar(64) NOT NULL COMMENT '联系人',
  `telephone` varchar(32) NOT NULL COMMENT '联系电话',
  `type` tinyint(4) DEFAULT NULL COMMENT '商家性质：1=生产商，2=代理商，3=加盟商',
  `describe` longtext COMMENT '品牌说明',
  `createTime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updateTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`supplierId`)
) ENGINE=InnoDB AUTO_INCREMENT=100000 DEFAULT CHARSET=utf8 COMMENT='供应商';
