/*==============================================================*/
drop table if exists seo_config;
create table seo_config 
(
   seoConfigId          int(11)                        not null  AUTO_INCREMENT,
   title                varchar(64)                    null,
   keyWords             varchar(128)                   null,
   description          varchar(256)                   null,
   urlKey               varchar(64)                    null,
   creatTime            timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
   urlMean				varchar(200)				   null,
   primary key (seoConfigId),
   unique (urlKey)
)ENGINE=InnoDB AUTO_INCREMENT=100000 DEFAULT CHARSET=utf8 COMMENT='seo';

