
/*==============================================================*/
/* Table: global_log                                            */
/*==============================================================*/
DROP TABLE IF EXISTS `global_log`;
CREATE TABLE `global_log` (
  `logId` int(11) NOT NULL AUTO_INCREMENT COMMENT '日志id',
  `logContent` text COMMENT '日志内容',
  `logType` tinyint(4) DEFAULT NULL COMMENT '日志类型',
  `logLevel` tinyint(4) DEFAULT NULL COMMENT '日志层级',
  `logCreateTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '日志创建时间',
  `logTime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '日志记录时间',
  PRIMARY KEY (`logId`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='全局日志';

/*==============================================================*/
/* Table: partner                                            */
/*==============================================================*/
DROP TABLE IF EXISTS `partner`;
CREATE TABLE `partner` (
  `partnerId` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) DEFAULT NULL COMMENT '名称',
  `unionId` varchar(255) DEFAULT NULL COMMENT '身份标识',
  `pushLink` varchar(255) DEFAULT NULL COMMENT '订单数据推送地址',
  `hash` varchar(255) DEFAULT NULL COMMENT '推送订单的hash值',
  `type` tinyint(4) DEFAULT NULL COMMENT '类型(1:CPS)',
  PRIMARY KEY (`partnerId`),
  UNIQUE KEY `unionId` (`unionId`)
) ENGINE=InnoDB AUTO_INCREMENT=100000 DEFAULT CHARSET=utf8 COMMENT='合作伙伴';

INSERT INTO partner VALUES (100000, '多麦', 'ec6d4ee41a904a6b83d094dde6821376', 'http://www.duomai.com/api/order.php', 'e196c1b174ecfdbe3b61a02a529c302e', 1);

/*==============================================================*/
/* Table: order_cps                                            */
/*==============================================================*/
DROP TABLE IF EXISTS `order_cps`;
CREATE TABLE `order_cps` (
  `orderCpsId` int(11) NOT NULL AUTO_INCREMENT,
  `orderId` int(11) NOT NULL,
  `commission` decimal(10,2) DEFAULT NULL COMMENT '佣金',
  `unionId` varchar(255) NOT NULL,
  `feedback` varchar(200) DEFAULT NULL COMMENT '网站主标识',
  `mid` varchar(255) DEFAULT NULL COMMENT '网站主的ID',
  `link` varchar(255) DEFAULT NULL COMMENT '推广链接',
  `isNewCustom` tinyint(4) DEFAULT NULL COMMENT '是否新客户(0:否,1:是)',
  `status` tinyint(4) DEFAULT '0' COMMENT '订单信息推送状态：0=未推送，1=推送成功，2=推送失败',
  `createTime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updateTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`orderCpsId`)
) ENGINE=InnoDB AUTO_INCREMENT=100000 DEFAULT CHARSET=utf8 COMMENT='订单CPS关联表';

