        </td>\r\n                                        <td>&nbsp;</td>\r\n                                      </tr>\r\n                                    </tbody>\r\n                                  </table>\r\n                                  </td>\r\n                                </tr>\r\n                              </tbody>\r\n                              </table>\r\n                              </td>\r\n                            <td width=\"420\">\r\n                            	<a href=\"${contextPath}/plan-${plan.planId?c}.htm\" target=\"_blank\">\r\n                            		<img border=\"0\" src=\"${thumbnail(plan.imageUrl, \'t13\')}\" alt=\"${plan.name}\" width=\"420\" height=\"180\">\r\n                            	</a>\r\n                            </td>\r\n                          </tr>\r\n                        </tbody></table></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"10\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td height=\"12\"></td>\r\n                    </tr>\r\n                    <tr>\r\n                      <td colspan=\"3\" align=\"right\"><a target=\"_blank\" href=\"${contextPath}/\" style=\"font-size:12px; line-height:18px; color:#fc7c00;text-decoration:underline;\"> 查看更多&gt;&gt;</a></td>\r\n                    </tr>\r\n                  </tbody>\r\n                </table></td>\r\n            </tr>\r\n            <tr>\r\n              <td height=\"30\"></td>\r\n            </tr>\r\n           </#list>\r\n            <tr>\r\n              <td height=\"15\"></td>\r\n            </tr>\r\n              <tr>\r\n              <td height=\"74\" valign=\"middle\" align=\"center\" style=\"border-top:1px solid #dfdfdf;\">\r\n              	<table style=\"padding:0 10px;\">\r\n              		<tr>\r\n              			<td width=\"600\" height=\"55\" align=\"left\" valign=\"middle\">\r\n              				<span style=\"font-siz